import {useEffect, useState} from 'react'
import {ProdutoList} from "./ProdutoList"
import {adicionarProduto, obterProdutos} from "../net/service"

function ProdutoNet () {
   
    const [items, setItems] = useState([]);
    const [nome, setNome] = useState('');
    const [quantidade, setQuantidade] = useState(0);
    const [conectado, setConectado] = useState(false);

    useEffect(()=>{
      if(!conectado){
        setConectado(true);
        obterProdutos().then((retorno)=>setItems(retorno));
      }
    });
    
    function handleNome(e) {
        setNome(e.target.value);
    }
    
    function handleQuantidade(e) {
      setQuantidade(e.target.value);
    }

    function handleSubmit(e) {
        e.preventDefault();
        if (nome.length === 0) {
          return;
        }
        adicionarProduto(nome,quantidade).then(
                 (retorno)=>
                      obterProdutos().then(
                          (retorno)=>setItems(retorno) 
                      ));
        setNome('');
    }
    
    return (
      <div>
        <h3>Produtos</h3>
        <ProdutoList items={items} />
        <form onSubmit={handleSubmit}>
          <label htmlFor="new-nome">
            Nome
          </label>
          <input
            id="new-nome"
            onChange={handleNome}
            value={nome}
          />
          <label htmlFor="new-qtd">
            Quantidade
          </label>
          <input
            id="new-qtd"
            onChange={handleQuantidade}
            value={quantidade}
          />
          <button>
            Adicionar
          </button>
        </form>
      </div>
    );
}

export default ProdutoNet;
